WorkflowProcessEvent.eventDescription:
function afterTaskComplete(colleagueId,nextSequenceId,userList){

	var atividade = getValue("WKNumState");
	var proxAtividade = getValue("WKNextState");
	var hora2 = obterDataCorrente2();
	var hora = obterDataCorrente();
	var nome = hAPI.getCardValue("currentNome");

	if(proxAtividade == 3 || proxAtividade == 5 || proxAtividade == 7 || proxAtividade == 9 || proxAtividade == 10){
		hAPI.setCardValue("Status", "Em Aprovação");
		hAPI.setCardValue("ultimoUsuario", nome);
		hAPI.setCardValue("hora", hora);
		hAPI.setCardValue("hora2", hora2);
	}
	if(proxAtividade == 26){
		hAPI.setCardValue("Status", "SP Reprovada");
		hAPI.setCardValue("ultimoUsuario", nome);
		hAPI.setCardValue("hora", hora);
		hAPI.setCardValue("hora2", hora2);
	}
	if(proxAtividade == 84){
		hAPI.setCardValue("Status", "Em Aprovação do Diretor Financeiro");
		hAPI.setCardValue("ultimoUsuario", nome);
		hAPI.setCardValue("hora", hora);
		hAPI.setCardValue("hora2", hora2);
	}
	if(proxAtividade == 12){
		hAPI.setCardValue("Status", "Encaminhado para Célula Fiscal");
		hAPI.setCardValue("ultimoUsuario", nome);
		hAPI.setCardValue("hora", hora);
		hAPI.setCardValue("hora2", hora2);
	}
	if(proxAtividade == 14){
		hAPI.setCardValue("Status", "Em análise TI");
		hAPI.setCardValue("ultimoUsuario", nome);
		hAPI.setCardValue("hora", hora);
		hAPI.setCardValue("hora2", hora2);
	}
	if(proxAtividade == 27 || proxAtividade == 28){
		hAPI.setCardValue("Status", "SP Encaminhada Para Pagamento");
		hAPI.setCardValue("ultimoUsuario", nome);
		hAPI.setCardValue("hora", hora);
		hAPI.setCardValue("hora2", hora2);
	}
	if(proxAtividade == 15){
		hAPI.setCardValue("Status", "SP - Pagamento Programado");
		hAPI.setCardValue("ultimoUsuario", nome);
		hAPI.setCardValue("hora", hora);
		hAPI.setCardValue("hora2", hora2);
	}
	if(proxAtividade == 20 || proxAtividade == 21 || proxAtividade == 22 || proxAtividade == 23 || proxAtividade == 24 || proxAtividade == 25 || proxAtividade == 87){
		hAPI.setCardValue("Status", "Cancelado");
		hAPI.setCardValue("ultimoUsuario", nome);
		hAPI.setCardValue("hora", hora);
		hAPI.setCardValue("hora2", hora2);
	}
}

function obterDataCorrente(){
	var dateCorrente = new Date();
	var formatoData = new java.text.SimpleDateFormat("dd/MM/yyyy HH:mm:ss");
	return formatoData.format(dateCorrente);
} // obterDataCorrente

function obterDataCorrente2(){
	var dateCorrente = new Date();
	var formatoData = new java.text.SimpleDateFormat("dd/MM/yyyy");
	return formatoData.format(dateCorrente);
} // obterDataCorrente

function afterProcessCreate(processId){
	// [Inicio] Campos de controle
	hAPI.setCardValue("numeroFluxo", processId);
	var emissao = obterDataCorrente();
	hAPI.setCardValue("dataCriacao", emissao);
	hAPI.setCardValue("situacao", "Novo");
	// [Fim] Campos de controle
}

function obterDataCorrente(){
	var dateCorrente = new Date();
	var formatoData = new java.text.SimpleDateFormat("dd/MM/yyyy HH:mm:ss");
	return formatoData.format(dateCorrente);
} // obterDataCorrente

function beforeCancelProcess(colleagueId,processId){
	var idUsuarioAtual = colleagueId;
	var comentario = getValue("WKUserComment");	
	var atividade = getValue("WKNumState");	
	
	log.info("############ SOLICITACAO DE CANCELAMENTO DA SOLICITAÇÃO: " + processId);
	log.info("############ USUARIO ATUAL: " + idUsuarioAtual);
	log.info("############ ATIVIDADE: " + atividade);
	
	if (idUsuarioAtual != "admin" && atividade == 28) { //Impede cancelamento na atividade do contas a Pagar. Somente o usuario "admin" possui acesso.
		throw "<br><br>" +
				"<b>Não é possível cancelar a solicitação nesta atividade.</b><br><br>" +
				"Dúvidas? Entre em contato com o departamento de TI. <br><br>"
	} else {
		//DISPARA EMAIL PARA O CONTAS A PAGAR
		log.info("############DISPARANDO EMAIL CUSTOMIZADO PARA O CONTAS A PAGAR:");
	    try {
	    	var emailTeste = "renato.avellar@criareconsulting.com";
	        var emailDestinatario = "contasapagar@rhmed.com.br";
	        var assunto = "RHMED|RHVIDA - Solicitação "+ processId + " CANCELADA";

	        var serverURL = fluigAPI.getPageService().getServerURL();
	        var link = "<a href='" + serverURL + "/portal/p/1/pageworkflowview?app_ecm_workflowview_detailsProcessInstanceID=" + processId + "' target='_blank'>" + processId + "</a>"
	        var corpoEmail = "A solicitação SPA - Adiantamento à Fornecedores, Colaboradores e Caixinha: " + link + " foi <b>CANCELADA</b> por: <b> " + idUsuarioAtual +"</b>."
	        
	        var parametros = new java.util.HashMap();
	        parametros.put("subject", assunto);
	        parametros.put("corpoEmail", corpoEmail);

	        //Monta lista de destinatários
	        var destinatarios = new java.util.ArrayList();
	        //destinatarios.add(emailTeste);
	        destinatarios.add(emailDestinatario);
	        
	        log.info("enviarEmail >> parametros " + parametros)
	        
	        //Envia e-mail
	        notifier.notify('admin', "template_email_RHMED_blank", parametros, destinatarios, "text/html");
	    } catch (err) {
	    	log.info("###########ERRO NO EMAIL CUSTOMIZADO PARA O CONTAS A PAGAR:");
	    	log.error(err);
	        throw(err)
	    }
	    
	    hAPI.setCardValue("Status", "Cancelado");
	}
}

